class Solution {
public:
    int countUnguarded(int m, int n, vector<vector<int>>& g, vector<vector<int>>& walls) {
        set<pair<int,int>> settu;//stores walls
        set<pair<int,int>> gua;//stores guards

        for(int i=0;i<walls.size();i++){
            settu.insert({walls[i][0],walls[i][1]});
        }

        for(int i=0;i<g.size();i++){
            gua.insert({g[i][0],g[i][1]});
        }

        set<pair<int,int>> unq;//stores cells that are guarded.

        for(auto it : g){
            int i = it[0]-1;
            int j = it[1];
            //below while loop is to move from i-1 to 0. that is moving up from the guard
            while(i>=0){
                if(gua.find({i,j})!=gua.end()){
                    break;//breaking if we found a guard.
                }
                if(settu.find({i,j})==settu.end()){
                    unq.insert({i,j});//include current cell if it is not a wall
                    i--;
                }else{
                    break;//break if it is a wall
                }
            }
            i = it[0]+1;
            //below while loop is to move from i+1 to m. that is moving down from the guard
            while(i<m){
                if(gua.find({i,j})!=gua.end()){
                    break;//breaking if we found a guard.
                }
                if(settu.find({i,j})==settu.end()){
                    unq.insert({i,j});//include current cell if it is not a wall
                    i++;
                }else{
                    break;//break if it is a wall
                }
            }
            j = it[1]-1;
            i = it[0];
            //below while loop is to move from j-1 to 0. that is moving left from the guard
            while(j>=0){
                if(gua.find({i,j})!=gua.end()){
                    break;//breaking if we found a guard.
                }
                if(settu.find({i,j})==settu.end()){
                    unq.insert({i,j});//include current cell if it is not a wall
                    j--;
                }else{
                    break;//break if it is a wall
                }
            }
            j = it[1]+1;
            //below while loop is to move from j+1 to n. that is moving right from the guard
            while(j<n){
                if(gua.find({i,j})!=gua.end()){
                    break;//breaking if we found a guard.
                }
                if(settu.find({i,j})==settu.end()){
                    unq.insert({i,j});//include current cell if it is not a wall
                    j++;
                }else{
                    break;//break if it is a wall
                }
            }

        }

        return m*n-settu.size()-gua.size()-unq.size();//finally return the remaining cells which are not guarded, guards, walls;
    }
};
