class Solution {
public:
    void helper(int m, int n, vector <vector <int>>& grid, int& count, int r, int c) {
        for (int i = r+1; i < m; i++) {
            if (grid[i][c] == 1 || grid[i][c] == 2) break;
            if (grid[i][c] == 0) {
                count++;
                grid[i][c] = 3;
            }
        }
        for (int i = r-1; i >= 0; i--) {
            if (grid[i][c] == 1 || grid[i][c] == 2) break;
            if (grid[i][c] == 0) {
                count++;
                grid[i][c] = 3;
            }
        }
        for (int i = c+1; i < n; i++) {
            if (grid[r][i] == 1 || grid[r][i] == 2) break;
            if (grid[r][i] == 0) {
                count++;
                grid[r][i] = 3;
            }
        }
        for (int i = c-1; i >= 0; i--) {
            if (grid[r][i] == 1 || grid[r][i] == 2) break;
            if (grid[r][i] == 0) {
                count++;
                grid[r][i] = 3;
            }
        }

    }
    int countUnguarded(int m, int n, vector<vector<int>>& guards, vector<vector<int>>& walls) {
        vector <vector <int>> grid(m, vector <int>(n, 0));
        // 0: free cell
        // 1: guard
        // 2: wall
        // 3: visited
        for (auto g : guards) {
            grid[g[0]][g[1]] = 1;
        }
        for (auto w : walls) {
            grid[w[0]][w[1]] = 2;
        }
        int count = 0;
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (grid[i][j] == 1) {
                    helper(m, n, grid, count, i, j);
                }
            }
        }
        return m*n - guards.size() - walls.size() - count;
    }
};

