class Solution {
    public int countUnguarded(int ROWS, int COLS, int[][] guards, int[][] walls) {
        int[][] prison = new int[ROWS][COLS];

        int prisonCells = ROWS * COLS;

        // Filling up prison with walls
        int walledCells = walls.length;
        for(int[] wall: walls) {
            prison[wall[0]][wall[1]] = 1;
        }

        // Filling up prison with guards
        int guardedCells = guards.length;
        for(int[] guard: guards) {
            prison[guard[0]][guard[1]] = 1;
        }

        // For each guard, marking view path
        for(int[] guard: guards) {
            int guardRow = guard[0];
            int guardCol = guard[1];

            // run to top, not counting guard's cell
            for(int row = guardRow - 1, col = guardCol; row >= 0 && prison[row][col] != 1;  row--) {
                if(prison[row][col] != 2) {  // to avoid counting view block cell twice
                    guardedCells++;
                }
                prison[row][col] = 2;
            }

            // run to bottom, not counting guard's cell
            for(int row = guardRow + 1, col = guardCol; row < ROWS && prison[row][col] != 1; row++) {
                if(prison[row][col] != 2) {  // to avoid counting view block cell twice
                    guardedCells++;
                }
                prison[row][col] = 2;
            }

            // run to left, not counting guard's cell
            for(int row = guardRow, col = guardCol - 1; col >= 0 && prison[row][col] != 1; col--) {
                if(prison[row][col] != 2) {  // to avoid counting view block cell twice
                    guardedCells++;
                }
                prison[row][col] = 2;
            }

            // run to right, not counting guard's cell
            for(int row = guardRow, col = guardCol + 1; col < COLS && prison[row][col] != 1; col++) {
                if(prison[row][col] != 2) {  // to avoid counting view block cell twice
                    guardedCells++;
                }
                prison[row][col] = 2;
            }

            // Comment out this line before submission - only for simulation
            print(prison, "After solving guard["+guard[0]+"]["+guard[1]+"]");

        }

        return prisonCells - (guardedCells + walledCells);
    }


    private void print(int[][] arr, String message) {
        System.out.println(message);
        for(int r = 0; r < arr.length; r++) {
            for(int c = 0; c < arr[0].length; c++) {
                System.out.print("  " + arr[r][c]);
            }
            System.out.println("");
        }
    }
}
```

