        }

        for(int i=0; i<g.size(); i++){
            int cx = g[i][0];
            int cy = g[i][1];
            for(int i=cx+1; i<m; i++){
                if(grid[i][cy]==2) break;
                grid[i][cy]=1;
            }
            for(int i=cx-1; i>=0; i--){
                if(grid[i][cy]==2) break;
                grid[i][cy]=1;
            }
            for(int j=cy+1; j<n; j++){
                if(grid[cx][j]==2) break;
                grid[cx][j]=1;
            }
            for(int j=cy-1; j>=0; j--){
                if(grid[cx][j]==2) break;
                grid[cx][j]=1;
            }
        }

        int ct=0;
        for(int i=0; i<m; i++){
            for(int j=0; j<n; j++){
                if(grid[i][j]==0) ct++;
            }
        }

        return ct;
    }
};
