class Solution {
public:
    int countUnguarded(int m, int n, vector<vector<int>>& g, vector<vector<int>>& w) {
        vector<vector<short int>> vec(m,vector<short int>(n,0));
		// to check all directions in which we have already travelled
        bool vis[m][n][4];
        memset(vis,false,sizeof(vis));
        for(auto i:w)
            vec[i[0]][i[1]]=-1;

        for(auto i:g)
        {
			// marking true at 0 index of vec[i][j] -> upward direction from this point is visited
            for(int j=i[0];j>=0;j--)
			{
				if(vec[j][i[1]]!=-1 && !vis[j][i[1]][0])
					vec[j][i[1]]=1,vis[j][i[1]][0]=1;
				else
					break;
			}
			// marking true at 1 index of vec[i][j] -> left direction from this point is visited
            for(int j=i[1]-1;j>=0;j--)
			{
				if(vec[i[0]][j]!=-1  && !vis[i[0]][j][1])
					vec[i[0]][j]=1,vis[i[0]][j][1]=1;
				else
					break;
			}
			// marking true at 2 index of vec[i][j] -> downward direction from this point is visited
            for(int j=i[0]+1;j<m;j++)
			{
				if(vec[j][i[1]]!=-1 && !vis[j][i[1]][2])
					vec[j][i[1]]=1,vis[j][i[1]][2]=1;
				else
					break;
			}
			// marking true at 3 index of vec[i][j] -> right direction from this point is visited
            for(int j=i[1]+1;j<n;j++)
			{
				if(vec[i[0]][j]!=-1 && !vis[i[0]][j][3])
					vec[i[0]][j]=1,vis[i[0]][j][3]=1;
				else
					break;
			}
        }
        int c=0;
        for(int i=0;i<m;i++)
            for(int j=0;j<n;j++)
                if(vec[i][j]==0)
                    c++;
        return c;
    }
};
