class Solution {
public:
    int countUnguarded(int m, int n, vector<vector<int>>& guards, vector<vector<int>>& walls) {
        vector<vector<int>> grid(m,vector<int>(n));
        for(int i=0;i<guards.size();i++){
            grid[guards[i][0]][guards[i][1]]=1;
        }
        for(int i=0;i<walls.size();i++){
            grid[walls[i][0]][walls[i][1]]=2;
        }
//         left to right
        auto temp1=grid,temp2=grid,temp3=grid,temp4=grid;
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                if(temp1[i][j]==2 || temp1[i][j]==1)
                    continue;
                if(j>0 && temp1[i][j-1]==1)
                    temp1[i][j]=1;
            }
        }
//      right to left
        for(int i=m-1;i>=0;i--){
            for(int j=n-1;j>=0;j--){
                if(temp2[i][j]==2 || temp2[i][j]==1)
                    continue;
                if(j<n-1 && temp2[i][j+1]==1)
                    temp2[i][j]=1;
            }
        }
//       top to down
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                if(temp3[i][j]==2 ||  temp3[i][j]==1)
                    continue;
                if(i>0 && temp3[i-1][j]==1)
                    temp3[i][j]=1;
            }
        }
//        down to top
        for(int i=m-1;i>=0;i--){
            for(int j=n-1;j>=0;j--){
                if(temp4[i][j]==2 || temp4[i][j]==1)
                    continue;
                if(i<m-1 && temp4[i+1][j]==1)
                    temp4[i][j]=1;
            }
        }
        int ans=0;
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                if(temp1[i][j]==0 && temp2[i][j]==0 && temp3[i][j]==0 && temp4[i][j]==0)
                    ans++;
            }
        }
        return ans;
    }
};
