        guarded = set()
        guards = set((r,c) for r,c in guards)
        walls = set((r,c) for r,c in walls)

        for R,C in guards:

            #right
            for c in range(C+1,COLS):
                if (R,c) in guards or (R,c) in walls:
                    break
                guarded.add((R,c))
            #left
            for c in range(C-1,-1,-1):
                if (R,c) in guards or (R,c) in walls:
                    break
                guarded.add((R,c))
            #up
            for r in range(R-1,-1,-1):
                if (r,C) in guards or (r,C) in walls:
                    break
                guarded.add((r,C))
            #down
            for r in range(R+1,ROWS):
                if (r,C) in guards or (r,C) in walls:
                    break
                guarded.add((r,C))

        return ROWS*COLS - len(guards) - len(walls) - len(guarded)

