    pub fn count_unguarded(M: i32, N: i32, G_: VVI, W_: VVI) -> i32 {
        let mut G = HashSet::new();
        let mut W = HashSet::new();
        let mut seen = HashSet::new();
        let key = (|i, j| format!("{},{}", i, j));
        for pair in G_ { G.insert(key(pair[0], pair[1])); }
        for pair in W_ { W.insert(key(pair[0], pair[1])); }
        let mut walk = (|row, col| {
            let k = key(row, col);
            seen.insert(k.clone());
            if W.contains(&k) {
                return;
            }
            for (di, dj) in vec![(-1, 0), (0, 1), (1, 0), (0, -1)] {
                let mut i = row;
                let mut j = col;
                while 0 <= i + di && i + di < M && 0 <= j + dj && j + dj < N {
                    i += di;
                    j += dj;
                    let k = key(i, j);
                    if G.contains(&k) || W.contains(&k) {
                        break;
                    }
                    seen.insert(k.clone());
                }
            }
        });
        for i in 0..M {
            for j in 0..N {
                let k = key(i, j);
                if G.contains(&k) || W.contains(&k) {
                    walk(i as i32, j as i32);
                }
            }
        }
        return M * N - seen.len() as i32;
    }
}
