Space complexity - ```O(M*N)```
```
class Solution {
public:
    int countUnguarded(int m, int n, vector<vector<int>>& guards, vector<vector<int>>& walls) {

        unordered_map<int, vector<int>> guardsRowMap, wallsRowMap, guardsColMap, wallsColMap;

        for (vector<int> guard : guards) {
            guardsRowMap[guard[0]].push_back(guard[1]);
            guardsColMap[guard[1]].push_back(guard[0]);
        }
        for (vector<int> wall : walls) {
            wallsRowMap[wall[0]].push_back(wall[1]);
            wallsColMap[wall[1]].push_back(wall[0]);
        }

        for (auto& item : guardsRowMap) {
            sort(item.second.begin(), item.second.end());
        }
        for (auto& item : wallsRowMap) {
            sort(item.second.begin(), item.second.end());
        }
        for (auto& item : guardsColMap) {
            sort(item.second.begin(), item.second.end());
        }
        for (auto& item : wallsColMap) {
            sort(item.second.begin(), item.second.end());
        }


        int count = 0;
        for (int row=0; row<m; row++) {
            for (int col=0; col<n; col++) {

                int guard_idx, wall_idx;

                guard_idx = lower_bound(guardsColMap[col].begin(), guardsColMap[col].end(), row) - guardsColMap[col].begin();
                if (guard_idx != guardsColMap[col].size() and guardsColMap[col][guard_idx] == row)    continue;

                wall_idx = lower_bound(wallsColMap[col].begin(), wallsColMap[col].end(), row) - wallsColMap[col].begin();
                if (wall_idx != wallsColMap[col].size() and wallsColMap[col][wall_idx] == row)  continue;

                if (guard_idx != 0 and 
                    (wall_idx == 0 or guardsColMap[col][guard_idx-1] > wallsColMap[col][wall_idx-1]))
                    continue;
                if (guard_idx != guardsColMap[col].size() and 
                    (wall_idx == wallsColMap[col].size() or guardsColMap[col][guard_idx] < wallsColMap[col][wall_idx]))
                    continue;

                guard_idx = lower_bound(guardsRowMap[row].begin(), guardsRowMap[row].end(), col) - guardsRowMap[row].begin();
                wall_idx = lower_bound(wallsRowMap[row].begin(), wallsRowMap[row].end(), col) - wallsRowMap[row].begin();

                if (guard_idx != 0 and 
                    (wall_idx == 0 or guardsRowMap[row][guard_idx-1] > wallsRowMap[row][wall_idx-1]))
                    continue;
                if (guard_idx != guardsRowMap[row].size() and 
                    (wall_idx == wallsRowMap[row].size() or guardsRowMap[row][guard_idx] < wallsRowMap[row][wall_idx]))
                    continue;
                count++;
            }
        }

        return count;
    }
};
