class Solution {
    public int countUnguarded(int m, int n, int[][] guards, int[][] walls) {
        // 1 -> guard, 2 -> wall
        int count = 0;
        TreeMap<Integer, Integer>[] row = new TreeMap[m];
        TreeMap<Integer, Integer>[] col = new TreeMap[n];
        Set<List<Integer>> set = new HashSet<>();

        for(int i = 0; i < m; i++) row[i] = new TreeMap<>();
        for(int i = 0; i < n; i++) col[i] = new TreeMap<>();

        for(int[] i : guards) {
            row[i[0]].put(i[1], 1);  
            col[i[1]].put(i[0], 1);
        }

        for(int[] i : walls) {
            row[i[0]].put(i[1], 2);
            col[i[1]].put(i[0], 2);
            set.add(List.of(i[0], i[1]));
        }

        for(int i = 0; i < m; i++){
            for(int j = 0; j < n; j++){
                if(set.contains(List.of(i, j))) continue;
                if((row[i].floorEntry(j) == null || row[i].floorEntry(j).getValue() == 2) &&
                    (row[i].ceilingEntry(j) == null || row[i].ceilingEntry(j).getValue() == 2) &&
                    (col[j].floorEntry(i) == null || col[j].floorEntry(i).getValue() == 2) &&
                   (col[j].ceilingEntry(i) == null || col[j].ceilingEntry(i).getValue() == 2))
                       count++;
            }
        }

        return count;
    }
}
