Other appraoches that i have seen are not linear. We are visiting one cell by multiple wall query. here we are iterating 4 time for each direction.
```
class Solution {
    public int countUnguarded(int m, int n, int[][] guards, int[][] walls) {
        //  guard right
        int[][] a = new int[m][n];
        //  guard left
        int[][] b = new int[m][n];
        //  guard top
        int[][] c = new int[m][n];
        //  guard bottom
        int[][] d = new int[m][n];

        //  making walls
        for (int[] p : walls) {
            a[p[0]][p[1]] = -1;
            b[p[0]][p[1]] = -1;
            c[p[0]][p[1]] = -1;
            d[p[0]][p[1]] = -1;
        }

        //  making guards
        for (int[] p : guards) {
            a[p[0]][p[1]] = 1;
            b[p[0]][p[1]] = 1;
            c[p[0]][p[1]] = 1;
            d[p[0]][p[1]] = 1;
        }

        //  right
        for (int j = 1; j < n; j++) {
            for (int i = 0; i < m; i++) {
                if (a[i][j] == 0) {
                    if (a[i][j - 1] == 1) {
                        a[i][j] = 1;
                    } else {
                        a[i][j] = 0;
                    }
                }
            }
        }

        //  left
        for (int j = n - 2; j >= 0; j--) {
            for (int i = 0; i < m; i++) {
                if (b[i][j] == 0) {
                    if (b[i][j + 1] == 1) {
                        b[i][j] = 1;
                    } else {
                        b[i][j] = 0;
                    }
                }
            }
        }


        //  top
        for (int i = 1; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (c[i][j] == 0) {
                    if (c[i - 1][j] == 1) {
                        c[i][j] = 1;
                    } else {
                        c[i][j] = 0;
                    }
                }
            }
        }

        // bottom
        for (int i = m - 2; i >= 0; i--) {
            for (int j = 0; j < n; j++) {
                if (d[i][j] == 0) {
                    if (d[i + 1][j] == 1) {
                        d[i][j] = 1;
                    } else {
                        d[i][j] = 0;
                    }
                }
            }
        }

        int answer = 0;
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (a[i][j] == 0 && b[i][j] == 0 && c[i][j] == 0 && d[i][j] == 0) {
                    answer++;
                }
            }
        }
        return answer;
    }
}

