class Solution {
public:

    static bool comp(pair<int,int> &a,pair<int,int> &b) {
        if(a.first!=b.first) return a.first<b.first;
        return a.second<b.second;

}

    int countUnguarded(int m, int n, vector<vector<int>>& guards, vector<vector<int>>& walls) {

        vector<vector<pair<int,int>>> p_row(m);
        vector<vector<pair<int,int>>> p_col(n);


        vector<vector<int>> v(m,vector<int> (n,1));

        for(int i=0;i<guards.size();i++){
            p_row[guards[i][0]].push_back({guards[i][1],0});
            p_col[guards[i][1]].push_back({guards[i][0],0}); 

            v[guards[i][0]][guards[i][1]]=-1;
        }

        for(int i=0;i<walls.size();i++){
            p_row[walls[i][0]].push_back({walls[i][1],1});
            p_col[walls[i][1]].push_back({walls[i][0],1}); 

            v[walls[i][0]][walls[i][1]]=-1;

        }

        for(int i=0;i<p_row.size();i++){
            sort(p_row[i].begin(),p_row[i].end());

        }
        for(int i=0;i<p_col.size();i++){
            sort(p_col[i].begin(),p_col[i].end());

        }


        long long count=0;


        for(int i=0;i<m;i++){

            for(int j=0;j<n;j++){

                int row=i;
                int col=j;
                if(v[row][col]==-1) continue;

                bool f=1;
                pair p={col,-1};
                int ind=lower_bound(p_row[row].begin(),p_row[row].end(),p)-p_row[row].begin();
                if(!(ind==p_row[row].size() || p_row[row][ind].second==1))
