class Solution {
public:
    //Traverse in all four directions, if possible then visit and mark them as guarded
        void traverse(vector<vector<int>> &mat, int i, int j) {
            int k;
            //right
            k = j+1;
            while(k < mat[0].size()) {
                if(mat[i][k] == 1 || mat[i][k] == 2) //If wall or guard we can't go furthur
                    break;
                mat[i][k] = 3;
                ++k;
            }
            //left
            k = j-1;
            while(k >= 0) {
                if(mat[i][k] == 1 || mat[i][k] == 2) //If wall or guard we can't go furthur
                    break;
                mat[i][k] = 3;
                --k;
            }

            //down
            k = i+1;
            while(k < mat.size()) {
                if(mat[k][j] == 1 || mat[k][j] == 2) //If wall or guard we can't go furthur
                    break;
                mat[k][j] = 3;
                ++k;
            }

            //up
            k = i-1;
            while(k >= 0) {
                if(mat[k][j] == 1 || mat[k][j] == 2) //If wall or guard we can't go furthur
                    break;
                mat[k][j] = 3;
                --k;
            }
        }

    int countUnguarded(int m, int n, vector<vector<int>>& guards, vector<vector<int>>& walls) {
        //0 means unoccupied
        //1 means guard
        //2 means wall
        //3 means guarded

        vector<vector<int>> mat(m, vector<int>(n, 0));

        //Mark all the guards
        for(int i = 0; i < guards.size(); ++i) {
            mat[guards[i][0]][guards[i][1]] = 1;
        }

        //Mark all the walls
        for(int i = 0; i < walls.size(); ++i) {
            mat[walls[i][0]][walls[i][1]] = 2;
        }

       //For each guard, try to guard cells by traversing in all four diections 
       for(int i = 0; i < m; ++i) {
            for(int j = 0; j < n; ++j) {
                if(mat[i][j] == 1) {
                    traverse(mat, i, j);
                }

            } 
        }

        //Count all the remaing cells, that cannot be guarded
        int unguarded = 0;
        for(int i = 0; i < m; ++i) {
            for(int j = 0; j < n; ++j) {
                if(mat[i][j] == 0) {
                    unguarded++;
                }
            }
        }

        return unguarded;
    }
};
