class Solution {
public:
    int countUnguarded(int m, int n, vector<vector<int>>& guards, vector<vector<int>>& walls) 
    {
        vector<vector<Units>> objects (m, vector<Units>(n, Units::GREEN));
        vector<vector<Units>> grids (m, vector<Units>(n, Units::GREEN));

        for (const auto& aWall : walls) {
            objects[aWall[0]][aWall[1]] = Units::WALL;
        }

        for (const auto& aGuard : guards) {
            objects[aGuard[0]][aGuard[1]] = Units::GUARD;
        }

        for (int i = 0; i < m; ++i) {
            Units lastCell = Units::GREEN;
            for (int j = 0; j < n; ++j)
            {
                recordOrFill(objects[i][j], lastCell, grids[i][j]);
            }

            lastCell = Units::GREEN;
            for (int j = n - 1; j >= 0; --j)
            {
                recordOrFill(objects[i][j], lastCell, grids[i][j]);
            }
        }

        for (int j = 0; j < n; ++j) {
            Units lastCell = Units::GREEN;
            for (int i = 0; i < m; ++i)
            {
                recordOrFill(objects[i][j], lastCell, grids[i][j]);
            }

            lastCell = Units::GREEN;
            for (int i = m - 1; i >= 0; --i)
            {
                recordOrFill(objects[i][j], lastCell, grids[i][j]);
            }
        }

        int result = 0;
        for (int i = 0; i < m; ++i) {
            for (int j = 0; j < n; ++j)
            {
                if (objects[i][j] == Units::GREEN && grids[i][j] == Units::GREEN)
                    ++result;
            }
        }

        return result;
    }

private:
    enum class Units : unsigned char {
        GREEN = 0,
        RED,
        GUARD,
        WALL
    };

    void recordOrFill(Units currCell, Units& lastCell, Units& gridCell) {
        if (currCell == Units::GUARD)
            lastCell = Units::GUARD;
        else if (currCell == Units::WALL)
            lastCell = Units::GREEN;

        if (lastCell == Units::GUARD)
            gridCell = Units::GUARD;
    }
};

