                int x = e[0],y = e[1];
                if(vis[x][y][i])continue;
                //if i == 1  then explore possible left-right;
                //if i == 0 then explore possible up-down;
                if(i == 0){
                    vis[x][y][i] = true;
                    for(int k = x - 1;k>=0;k--){
                        if(mp[{k,y}])break;
                        vis[k][y][i] = true;
                    }
                    for(int k = x + 1;k<m;k++){
                        if(mp[{k,y}])break;
                        vis[k][y][i] = true;
                    }
                }
                else{
                    vis[x][y][i] = true;
                    for(int k = y - 1;k>=0;k--){
                        if(mp[{x,k}])break;
                        vis[x][k][i] = true;
                    }
                    for(int k = y + 1;k<n;k++){
                        if(mp[{x,k}])break;
                        vis[x][k][i] = true;
                    }
                }
            }
        }
        int ans = 0;

        for(int i = 0;i<m;i++){
            for(int j = 0;j<n;j++){
                if(mp[{i,j}])continue;
                if(!(vis[i][j][0] || vis[i][j][1]))ans++;
            }
        }
        return ans;

    }
};
