class Solution {
    public int countUnguarded(int m, int n, int[][] guards, int[][] walls) {
        int[][] grid = new int[m][n];
        for(int[] i:grid){
            Arrays.fill(i,-1);
        }
        for(int i[]:guards){
            grid[i[0]][i[1]] = 1;
        }
        for(int i[]:walls){
            grid[i[0]][i[1]] = 2;
        }
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                if(grid[i][j]==1) helper(i,j,grid);
            }
        }
        int answer = 0;
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++) if(grid[i][j]==-1) answer++;
        }
        return answer;
    }
    public void helper(int row,int col,int[][] grid){
        int m = grid.length;
        int n = grid[0].length;
        for(int i=col+1;i<n;i++){
            if(grid[row][i]==-1) grid[row][i] = 3;
            if(grid[row][i]==2||grid[row][i]==1) break;
        }
        for(int i=col-1;i>=0;i--){
            if(grid[row][i]==-1) grid[row][i] = 3;
            if(grid[row][i]==2||grid[row][i]==1) break;
        }
        for(int i=row+1;i<m;i++){
            if(grid[i][col]==-1) grid[i][col] = 3;
            if(grid[i][col]==2||grid[i][col]==1) break;
        }
        for(int i=row-1;i>=0;i--){
            if(grid[i][col]==-1) grid[i][col] = 3;
            if(grid[i][col]==2||grid[i][col]==1) break;
        }
    }
}
