class Solution {
    func countUnguarded(_ m: Int, _ n: Int, _ guards: [[Int]], _ walls: [[Int]]) -> Int {

        var cells = Array(repeating: Array(repeating: 1, count: n), count: m)

        for w in walls { cells[w[0]][w[1]] = -1 }
        for g in guards { cells[g[0]][g[1]] = -1 }

        for g in guards {

            for x in (0..<g[0]).reversed() {
                guard cells[x][g[1]] != -1 else { break }
                cells[x][g[1]] = 0
            }

            for x in (g[0]..<m).dropFirst() {
                guard cells[x][g[1]] != -1 else { break }
                cells[x][g[1]] = 0
            }

            for y in (0..<g[1]).reversed() {
                guard cells[g[0]][y] != -1 else { break }
                cells[g[0]][y] = 0
            }

            for y in (g[1]..<n).dropFirst() {
                guard cells[g[0]][y] != -1 else { break }
                cells[g[0]][y] = 0
            }
        }

        return cells
            .map { $0.reduce(0) { $0 + max($1, 0) } }
            .reduce(0, +)
    }
}
