class Solution {
public:
    int countUnguarded(int m, int n, vector<vector<int>>& guards, vector<vector<int>>& walls) {
        int cnt = 0;

        // create a visited array to mark those cells which are guarded by the guards.
        vector<vector<int>> vis(m,vector<int>(n,0));

        // mark the guard's positions as 2
        for(auto& it:guards) {
            vis[it[0]][it[1]] = 2;
        }

        // mark the walls's positions as 3    
        for(auto& it:walls) {
            vis[it[0]][it[1]] = 3;
        }

        for(auto& it:guards) {
            // mark row if possible
            // mark left part
            int r,c;
            r = it[0];
            c = it[1]-1;
            while(c>=0) {
                if(vis[r][c]==2 || vis[r][c]==3) break;
                else if(vis[r][c]==0) vis[r][c]=1;
                c--;
            }

            // mark right part
            c = it[1]+1;
            while(c<n) {
                if(vis[r][c]==2 || vis[r][c]==3) break;
                else if(vis[r][c]==0) vis[r][c]=1;
                c++;
            }

            // mark top part
            r = it[0]-1;
            c = it[1];
            while(r>=0) {
                if(vis[r][c]==2 || vis[r][c]==3) break;
                else if(vis[r][c]==0) vis[r][c]=1;
                r--;
            }

            // mark bottom part
            r = it[0]+1;
            c = it[1];
            while(r<m) {
                if(vis[r][c]==2 || vis[r][c]==3) break;
                else if(vis[r][c]==0) vis[r][c]=1;
                r++;
            }
        }

        // count unguarded cells
        for(int i=0;i<m;i++) {
            for(int j=0;j<n;j++) {
                if(vis[i][j]==0) cnt++;
            }
        }
        return cnt;
    }
};
