            int x = guards[i][0];
            int y = guards[i][1];


            for(int j=x-1;j>=0;j--) {
                if(space[j][y]==-1 || space[j][y]==1) {
                    break;
                }
                space[j][y]=5;
            }

            for(int j=x+1;j<m;j++) {
                if(space[j][y]==-1 || space[j][y]==1) {
                    break;
                }
                space[j][y]=5;
            }

            for(int j=y-1;j>=0;j--) {
                if(space[x][j]==-1 || space[x][j]==1) {
                    break;
                }
