class Solution {
    enum class CellState {
        Guard,
        Wall,
        GuardedV,
        GuardedH,
        GuardedBoth,
        Unguarded
    }

    fun countUnguarded(m: Int, n: Int, guards: Array<IntArray>, walls: Array<IntArray>): Int {
		// Simulated grid
        val grid = Array<Array<CellState>>(m) { Array<CellState>(n) { CellState.Unguarded } }

		// Placing walls
        for (wall in walls) grid[wall[0]][wall[1]] = CellState.Wall

		// Traversing the row
        fun guardHorizontal(x: Int, y: Int) {
			// Ensure we are within bounds
            if (x < 0 || x >= grid.size) return
            if (y < 0 || y >= grid[x].size) return

			// Stop moving when hit another guard or wall
            if (grid[x][y] == CellState.Guard || grid[x][y] == CellState.Wall) return

			// If the cell is already guarded by a guard on this row, we can stop moving
            if (grid[x][y] == CellState.GuardedH || grid[x][y] == CellState.GuardedBoth) return

			// If the cell is guarded by a guard on the crossing column mark cell as guarded both horizontally and vertically
			// Otherwise mark cell as guarded only horizontally
            grid[x][y] = if (grid[x][y] == CellState.Unguarded) CellState.GuardedH else CellState.GuardedBoth

			// Move both left and right from current cell
            guardHorizontal(x, y - 1)
            guardHorizontal(x, y + 1)
        }

		// Traversing the column
        fun guardVertical(x: Int, y: Int) {
			// Ensure we are within bounds
            if (x < 0 || x >= grid.size) return
            if (y < 0 || y >= grid[x].size) return

			// Stop moving when hit another guard or wall
            if (grid[x][y] == CellState.Guard || grid[x][y] == CellState.Wall) return

			// If the cell is already guarded by a guard on this column, we can stop moving
            if (grid[x][y] == CellState.GuardedV || grid[x][y] == CellState.GuardedBoth) return

			// If the cell is already guarded by a guard on the crossing row mark cell as guarded both horizontally and vertically
			// Otherwise mark cell as guarded only vertically
            grid[x][y] = if (grid[x][y] == CellState.Unguarded) CellState.GuardedV else CellState.GuardedBoth

			// Move both up and down from current cell
            guardVertical(x + 1, y)
            guardVertical(x - 1, y)
        }

        // Guarding cells
        for (guard in guards) {
            grid[guard[0]][guard[1]] = CellState.Guard

			// Move up and down from guard's position to mark cells as guarded
            guardVertical(guard[0] - 1, guard[1])
            guardVertical(guard[0] + 1, guard[1])

			// Move left and right from guard's position to mark cells as guarded
            guardHorizontal(guard[0], guard[1] - 1)
            guardHorizontal(guard[0], guard[1] + 1)
        }

        // Counting unguarded cells
        var count = 0
        for (x in grid.indices)
            for (y in grid[x].indices) if (grid[x][y] == CellState.Unguarded) count++
        return count
    }
}
