class Solution {
    public int countUnguarded(int m, int n, int[][] guards, int[][] walls) {
        int[][] ground = new int[m][n];

        for(int[] guard : guards){
            ground[guard[0]][guard[1]] = 1;
        }
        for(int[] wall : walls){
            ground[wall[0]][wall[1]] = 2;
        }

        for(int[] guard : guards){
            processGround(guard[0], guard[1], ground, m, n);
        }
        int count = 0;

        for(int i = 0; i < m; i++){
            for(int j = 0; j < n; j++){
                if(ground[i][j] == 0)
                    count++;
            }
        }

        return count;
    }

    void processGround(int i, int j, int[][] ground, int m, int n){
        //Process TOP
        int tempI = i - 1; 
        while(tempI >= 0){
            if(ground[tempI][j] == 0){
                ground[tempI][j] = 3;
            } else if(ground[tempI][j] != 3){
                break;
            }
            tempI--;
        }

        //Process DOWN
        tempI = i + 1; 
        while(tempI < m){
            if(ground[tempI][j] == 0){
                ground[tempI][j] = 3;
            } else if(ground[tempI][j] != 3){
                break;
            }
            tempI++;
        }

        //Process LEFT
        int tempJ = j - 1; 
        while(tempJ >= 0){
            if(ground[i][tempJ] == 0){
                ground[i][tempJ] = 3;
            } else if(ground[i][tempJ] != 3){
                break;
            }
            tempJ--;
        }

        //Process RIGHT
        tempJ = j + 1; 
        while(tempJ < n){
            if(ground[i][tempJ] == 0){
                ground[i][tempJ] = 3;
            } else if(ground[i][tempJ] != 3){
                break;
            }
            tempJ++;
        }
    }
}
