class Solution {
    public int countUnguarded(int m, int n, int[][] guards, int[][] walls) {

    //2 is for guards
    //1 is for walls

    int[][]grid=new int[m][n];

    //filling up the coordinates of guarded cell
    for(int i=0;i<guards.length;i++){
        grid[guards[i][0]][guards[i][1]]=2;
    }
    //filling up the coordinates of walls cell
    for(int i=0;i<walls.length;i++){
        grid[walls[i][0]][walls[i][1]]=1;
    }

    for(int i=0;i<grid.length;i++){

        for(int j=0;j<grid[0].length;j++){

        if(grid[i][j]==2){

        //for up
        int p=i-1;
        int o=j;
        while(p>=0&&(grid[p][o]==0||grid[p][o]==-1)){
            grid[p][o]=-1;
            p--;
        }
        //for down
        p=i+1;
        o=j;
        while(p<grid.length&&(grid[p][o]==0||grid[p][o]==-1)){
            grid[p][o]=-1;
            p++;
        }
        //for left
        p=i;
        o=j-1;
        while(o>=0&&(grid[p][o]==0||grid[p][o]==-1)){
            grid[p][o]=-1;
            o--;
        }
        //for right
        p=i;
        o=j+1;
        while(o<grid[0].length&&(grid[p][o]==0||grid[p][o]==-1))
        {
            grid[p][o]=-1;
            o++;
        }

        }

        }
    }

    int count=0;

    for(int i=0;i<grid.length;i++){
        for(int j=0;j<grid[0].length;j++){
            if(grid[i][j]==0)count++;
        }
    }

    return count;

    }
}
