class Solution {
public:
    void right(int i,int j,int m,int n,vector<vector<char>>&arr,vector<vector<bool>>&visited){

        if(i<0 || j<0 || i>=m || j>=n || arr[i][j]=='W' || arr[i][j]=='G'){
            return;
        }
        visited[i][j]=true;
        right(i,j+1,m,n,arr,visited);
    }
    void left(int i,int j,int m,int n,vector<vector<char>>&arr,vector<vector<bool>>&visited){

        if(i<0 || j<0 || i>=m || j>=n || arr[i][j]=='W' || arr[i][j]=='G'){
            return;
        }
        visited[i][j]=true;
        left(i,j-1,m,n,arr,visited);
    }
    void down(int i,int j,int m,int n,vector<vector<char>>&arr,vector<vector<bool>>&visited){

        if(i<0 || j<0 || i>=m || j>=n || arr[i][j]=='W' || arr[i][j]=='G'){
            return;
        }
        visited[i][j]=true;
        down(i+1,j,m,n,arr,visited);
    }
    void up(int i,int j,int m,int n,vector<vector<char>>&arr,vector<vector<bool>>&visited){

        if(i<0 || j<0 || i>=m || j>=n || arr[i][j]=='W' || arr[i][j]=='G'){
            return;
        }
        visited[i][j]=true;
        up(i-1,j,m,n,arr,visited);
    }
    int countUnguarded(int m, int n, vector<vector<int>>& guards, vector<vector<int>>& walls) {

        vector<vector<char>> arr(m,vector<char>(n,'.'));
        for(int i=0;i<guards.size();i++){

            int row=guards[i][0];
            int col=guards[i][1];
            arr[row][col]='G';
        }
        for(int i=0;i<walls.size();i++){

            int row=walls[i][0];
            int col=walls[i][1];
            arr[row][col]='W';
        }
        vector<vector<bool>> visited(m,vector<bool>(n,false));
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){

                if(arr[i][j]=='G'){

                    up(i-1,j,m,n,arr,visited);
                    down(i+1,j,m,n,arr,visited);
                    left(i,j-1,m,n,arr,visited);
                    right(i,j+1,m,n,arr,visited);
                }
            }
        }
        long long count=0;
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){

                if(arr[i][j]!='G' && arr[i][j]!='W' && visited[i][j]!=true){
                    count++;
                }
            }
        }
        return count;
    }
