public:
    int countUnguarded(int m, int n, vector<vector<int>>& guards, vector<vector<int>>& walls) {
        vector<string> cells(m, string(n, 'u')); // unguarded
        int res = m*n;
        for ( vector<int>& w: walls ){
            --res;
            cells[w[0]][w[1]] = 'w';
        }
        for ( vector<int>& g: guards ){
            --res;
            cells[g[0]][g[1]] = 'g';
        }
        for ( vector<int>& g: guards ){
            bool stop = false;
            for ( int i = g[0]-1; ~i && !stop ; --i ){
                char& c = cells[i][g[1]];
                switch ( c ){
                    case 'u': {--res; c = 'v';} break;
                    case 'h': c = 'b'; break;
                    default: stop = true;
                }
            }
            stop = false;
            for ( int i = g[0]+1; i != m && !stop ; ++i ){
                char& c = cells[i][g[1]];
                switch ( c ){
                    case 'u': {--res; c = 'v';} break;
                    case 'h': c = 'b'; break;
                    default: stop = true;
                }
            }
            stop = false;
            for ( int j = g[1]-1; ~j && !stop ; --j ){
                char& c = cells[g[0]][j];
                switch ( c ){
                    case 'u': {--res; c = 'h';} break;
                    case 'v': c = 'b'; break;
                    default: stop = true;
                }
            }
            stop = false;
            for ( int j = g[1]+1; j != n && !stop ; ++j ){
                char& c = cells[g[0]][j];
                switch ( c ){
                    case 'u': {--res; c = 'h';} break;
                    case 'v': c = 'b'; break;
                    default: stop = true;
                }
            }
        }
        return res;
    }
};
