const Unguarded = 0
const (
	GuardedHorizontally = 1 << iota
	GuardedVertically   = 1 << iota
)

func countUnguarded(m int, n int, guards [][]int, walls [][]int) int {
	grid := make([][]byte, m)
	for i := 0; i < m; i++ {
		grid[i] = make([]byte, n)
	}
	for _, wall := range walls {
		grid[wall[0]][wall[1]] = GuardedHorizontally | GuardedVertically
	}
	for _, guard := range guards {
		i, j := guard[0], guard[1]
		grid[i][j] = GuardedHorizontally | GuardedVertically
		for i := i - 1; i >= 0 && grid[i][j]&GuardedVertically == Unguarded; i-- {
			grid[i][j] |= GuardedVertically
		}
		for i := i + 1; i < m && grid[i][j]&GuardedVertically == Unguarded; i++ {
			grid[i][j] |= GuardedVertically
		}
		for j := j - 1; j >= 0 && grid[i][j]&GuardedHorizontally == Unguarded; j-- {
			grid[i][j] |= GuardedHorizontally
		}
		for j := j + 1; j < n && grid[i][j]&GuardedHorizontally == Unguarded; j++ {
			grid[i][j] |= GuardedHorizontally
		}
	}
	var result int
	for _, row := range grid {
		for _, cell := range row {
			if cell == Unguarded {
				result++
			}
		}
	}
	return result
}
