class Solution {
    public int countUnguarded(int n, int m, int[][] guards, int[][] walls) {
        int grid[][]=new int[n][m];
        for(int guard[]:guards)
            grid[guard[0]][guard[1]]=2;
        for(int wall[]:walls)
            grid[wall[0]][wall[1]]=-2;
        for(int guard[]:guards){
                dfs(guard[0]-1, guard[1],grid,n,m,1);
                dfs(guard[0],guard[1]+1,grid,n,m, 2);
                dfs(guard[0]+1, guard[1],grid,n,m,3);
                dfs(guard[0], guard[1]-1,grid,n,m,4);
        }
       int count = 0;
        for(int i = 0; i < n; i++)
            for(int j = 0; j < m; j++)
                if(grid[i][j]!=-1 && grid[i][j] != -2 && grid[i][j] != 2)
                    count++;

        return count;
    }
    void dfs(int i,int j,int grid[][],int n,int m,int val){
        if(i>=n ||i<0||j<0||j>=m ||grid[i][j]==2||grid[i][j]==-2)
            return ;
        // if(grid[i][j]==-1)
        //     return;
        //we are not writing the above statement as
          /////    -1
        // G -1 -1 -1 -1 -1 W
        //         -1
        //(when we reach the mid point if we return we will not go down)
        grid[i][j]=-1;//marking it visited
        if(val==1)
            dfs(i-1,j,grid,n,m,1);
        else if(val==2)
            dfs(i,j+1,grid,n,m,2);
        else if(val==3)
            dfs(i+1,j,grid,n,m,3);
        else
            dfs(i,j-1,grid,n,m,4);
            return ;
    }
}
