int countUnguarded(int m, int n, vector<vector<int>>& guards, vector<vector<int>>& walls)    
{
	count=0;
    mat.resize(m,vector<char>(n,' '));
	for(int i=0;i<guards.size();i++)
	{
	    int r = guards[i][0];
	    int c = guards[i][1];
	    mat[r][c] = 'g';
    }
	for(int i=0;i<walls.size();i++)
	{
	    int r = walls[i][0];
	    int c = walls[i][1];
	    mat[r][c] = 'w';
    }       
	//if some empty cell is being guarded by some other guard it would mark it as '*"   
    for(int i=0;i<mat.size();i++)
    {
    	for(int j=0;j<mat[i].size();j++)
    	{
    		if(mat[i][j]==' ' or  mat[i][j]=='*' or mat[i][j]=='w')
    		{
    			continue;
			}
			else if(mat[i][j] == 'g')
			{
			    fun(i,j);
			}
		}
	}
	for(int i=0;i<mat.size();i++)
	{
		for(int j=0;j<mat[i].size();j++)
		{
			if(mat[i][j] == ' ')
			{
				count++;
			}
		}
	}
	return count;
}
void fun(int row,int col)
{
	int temp_row;
	int temp_col;

	//checking up
	temp_row = row;
	temp_col = col;

	while(temp_row >= 0)
	{
        temp_row--;

        if(temp_row < 0)
        break;

        if((temp_row!=row or temp_col!=col) and (mat[temp_row][temp_col]=='g' or mat[temp_row][temp_col]=='w'))
		break;

		mat[temp_row][temp_col] = '*';
	}

	//checking down
	temp_row = row;
	temp_col = col;
	while(temp_row < mat.size())
	{
        temp_row++;

        if(temp_row >= mat.size())
        break;

		if((temp_row!=row or temp_col!=col) and (mat[temp_row][temp_col]=='g' or mat[temp_row][temp_col]=='w'))
		break;

        mat[temp_row][temp_col] = '*';
	}

	//checking left
	temp_row = row;
	temp_col = col;
	while(temp_col>=0)
	{
		temp_col--;

        if(temp_col < 0)
        break;

		if((temp_row!=row or temp_col!=col) and (mat[temp_row][temp_col]=='g' or mat[temp_row][temp_col]=='w'))
		break;

        mat[temp_row][temp_col] = '*';
	}

	//checking right
	temp_row = row;
	temp_col = col;
	while(temp_col < mat[0].size())
	{
		temp_col++;

        if(temp_col >= mat[0].size())
        break;

		if((temp_row!=row or temp_col!=col) and (mat[temp_row][temp_col]=='g' or mat[temp_row][temp_col]=='w'))
		break;

        mat[temp_row][temp_col] = '*';
	}

	mat[row][col] = 'g';
}
