```go
func countUnguarded(m int, n int, guards [][]int, walls [][]int) int {
  mat := make([][]int, m)
  for i := 0; i < m; i++ {
    mat[i] = make([]int, n)
  }
  var occupied int
  for _, wall := range walls {
    mat[wall[0]][wall[1]] = -1
    occupied++
  }
  // We mark the matrix with bitmasking of whether horizontal or vertical
  // coverage present at a specific cell.
  // 01 - horizontal
  // 10 - vertical
  // 11 - both horizontal and vertical
  // 00 - no coverage
  directions := [4][2]int{{0, 1}, {1, 0}, {0, -1}, {-1, 0}}
  markers := [4]int{1, 2, 1, 2}
  for _, guard := range guards {
    mat[guard[0]][guard[1]] = 3 // Coverage at both horizontal and vertical
    occupied++
  }
  // Now let's mark all the positions that each guard can watch
  for _, guard := range guards {
    for idx, dir := range directions {
      x, y := guard[0] + dir[0], guard[1] + dir[1]
      for x >= 0 && x < m && y >= 0 && y < n && mat[x][y] != -1 && mat[x][y] & markers[idx] == 0 {
        if mat[x][y] == 0 {
          occupied++
        }
        mat[x][y] = mat[x][y] | markers[idx]
        x, y = x + dir[0], y + dir[1]
      }
    }
  }
  return m*n - occupied
}
