    enum State{
        NO,
        YES,
        GUARD,
        WALL
    };

    int countUnguarded(int m, int n, vector<vector<int>>& guards, vector<vector<int>>& walls) {
        auto mat = vector(m, vector(n, State::NO));
        for(auto& v:guards) mat[v[0]][v[1]] = GUARD;
        for(auto& v:walls) mat[v[0]][v[1]] = WALL;

        auto op = [&](int i, int j, State& pre) mutable {
            auto& x = mat[i][j];
            if (x == NO){
                if (pre == GUARD) {
                    x = YES;
                }
            }
            else if (x == GUARD || x == WALL) pre = x;
        };

        for(int j=0;j<n;j++){
            State pre = WALL;
            for(int i=0; i<m; i++) op(i, j, pre);
            pre = WALL;
            for(int i=m-1; i>=0; i--) op(i, j, pre);
        }

        for(int i=0;i<m;i++){
            State pre = WALL;
            for(int j=0; j<n; j++)  op(i, j, pre);
            pre = WALL;
            for(int j=n-1; j>=0; j--) op(i, j, pre);
        }        

        int res = 0;
        for(auto& v:mat) res += count(v.begin(), v.end(), State::NO);        
        return res;
    }

