Can cut off some portion of work if you stop unidirectional DFS search when you reach another guard. (they'll take care of the rest themselves). 

```
class Solution {
public:
    vector<pair<int,int>> dirs = {{-1,0}, {1, 0}, {0,-1}, {0, 1}};

    int countUnguarded(int m, int n, vector<vector<int>>& guards, vector<vector<int>>& walls) {
        vector<vector<char>> map (m, vector<char>(n, ' '));
        for(auto& v: guards) map[v[0]][v[1]] = 'G';
        for(auto& v: walls) map[v[0]][v[1]] = 'W';
        for(auto& v: guards){
            map[v[0]][v[1]] = 'X';
            for(int i = 0; i < 4; i++) dfs(m, n, v[0], v[1], map, i);
            map[v[0]][v[1]] = 'G';
        }
        int cnt = 0;
        for(auto& v: map) cnt += count( v.begin(), v.end(), ' ');
        return cnt;
    }

    void dfs(int m, int n, int x, int y, vector<vector<char>>& map, int ind){
