    int countUnguarded(int m, int n, vector<vector<int>>& guards, vector<vector<int>>& walls) {
        vector<vector<int>> mp(m, vector<int>(n));   //Create a array of m*n with defau;t zero value..
        for(int i=0;i<guards.size();i++)
            mp[guards[i][0]][guards[i][1]]=1;
        for(int i=0;i<walls.size();i++)
            mp[walls[i][0]][walls[i][1]]=2;
        //For each guard in all four direction move until we reach end or end upseeing a wall updating them as no go field(-1).
         for(auto x: guards)
         {
             bool l=true,r=true,d=true,u=true;
            for(int i=1;i<max(m,n) && (l || u || d || r);i++)  // checking of all bool value is important as it reduces the visits by a huge number
            {
                if(l && x[0]-i>=0 && mp[x[0]-i][x[1]]!=2 && mp[x[0]-i][x[1]]!=1)
                    mp[x[0]-i][x[1]]=-1;
                else
                    l=false;             
                if(r && x[0]+i<m && mp[x[0]+i][x[1]]!=2 && mp[x[0]+i][x[1]]!=1)
                    mp[x[0]+i][x[1]]=-1;
                else
                    r=false;                
                if(u && x[1]-i>=0 && mp[x[0]][x[1]-i]!=2  && mp[x[0]][x[1]-i]!=1)
                    mp[x[0]][x[1]-i]=-1;
                else
                    u=false;
                if(d && x[1]+i<n && mp[x[0]][x[1]+i]!=2 && mp[x[0]][x[1]+i]!=1)
                    mp[x[0]][x[1]+i]=-1;
                else
                    d=false;
            }
         }        
        int ans=0;   // Once we mark all the guarded places we can now see how many unguarded positions are there.. 
		//conversly in above loop we can count the guarded and add them with number of  walls and guards and subract with m*N to get answers
        for(int i=0;i<m;i++)
            for(int j=0;j<n;j++)
                if(mp[i][j]==0)
                    ans++;
        return ans;
    }
};
