#define ll long long
class Solution {
public:
    int countUnguarded(int m, int n, vector<vector<int>>& guards, vector<vector<int>>& walls) {
        ll count=0;
        vector<vector<int>>grid(m,vector<int>(n,0));
        //guard=>-2
        //wall=>2
        //greens=>1
        //reds=>0
        for(auto v:guards)
        {
            grid[v[0]][v[1]]=-2;
        }
        for(auto v:walls)
        {
            grid[v[0]][v[1]]=2;
        }
        //traversing row wise
        for(int i=0;i<m;i++)
        {
            //traversing as the guard looks only to his right
            bool lastIsWall=true;
            for(int j=0;j<n;j++)
            {
                if(grid[i][j]==-2)
                {
                    lastIsWall=false;
                }
                else if(grid[i][j]==2)
                {
                    lastIsWall=true;
                }
                else if(lastIsWall)
                    grid[i][j]=1;
                else
                    grid[i][j]=0;
            }

            //traversing as the guard is looking towards his left and we do (left & right)
            //green and red ==>red
            //(only) green and green==>green 

            lastIsWall=true;
            for(int j=n-1;j>=0;j--)
            {
                if(grid[i][j]==-2)
                {
                    lastIsWall=false;
                }
                else if(grid[i][j]==2)
                {
                    lastIsWall=true;
                }
                else if(lastIsWall)
                    grid[i][j]=grid[i][j] & 1;
                else
                    grid[i][j]=0;
            }           

        }
        //traversing column wise
        for(int j=0;j<n;j++)
        {
            //traversing as if the guard can only see up and taking the and
            bool lastIsWall=true;
            for(int i=0;i<m;i++)
            {
                if(grid[i][j]==-2)
                {
                    lastIsWall=false;
                }
                else if(grid[i][j]==2)
                {
                    lastIsWall=true;
                }
                else if(lastIsWall)
                    grid[i][j]=grid[i][j] & 1;
                else
                    grid[i][j]=0;
            }

            //traversing as if the guard is looking down and keeping the answer as and of all
            lastIsWall=true;
            for(int i=m-1;i>=0;i--)
            {
                if(grid[i][j]==-2)
                {
                    lastIsWall=false;
                }
                else if(grid[i][j]==2)
                {
                    lastIsWall=true;
                }
                else if(lastIsWall)
                    grid[i][j]=grid[i][j] & 1;
                else
                    grid[i][j]=0;
            }           

        }
        //now we count the greens i.e. 1's
        for(int i=0;i<m;i++)
        {
            for(int j=0;j<n;j++)
            {
                // cout<<grid[i][j]<<" ";
                if(grid[i][j]==1)
                    count++;
            }
            // cout<<endl;
        }
        return count;

    }
};

