class Solution:
    def countUnguarded(self, m: int, n: int, guards: List[List[int]], walls: List[List[int]]) -> int:
        self.grid = [[0]*n for item in range(m)]
        for item in guards:
            self.grid[item[0]][item[1]]='G'
        for item in walls:
            self.grid[item[0]][item[1]]='W'
        for item in guards:
            i, j = item
            # print('Called from this',i,j)
            self.down_break(i+1,j)
            self.up_break(i-1,j)
            self.left_break(i,j-1)
            self.right_break(i,j+1)
                    # print(self.grid)
        count=0
        for i in range(m):
            for j in range(n):
                if self.grid[i][j]==0:
                    count+=1
        return count


    def up_break(self,r,k):
        if r<0 or r>=len(self.grid) or k>=len(self.grid[0]) or k<0 or self.grid[r][k] in ['G', 'W']:
            return
        # print('Called up', r,k)
        self.grid[r][k]='X'
        self.up_break(r-1,k)


    def down_break(self,r,k):
        if r<0 or r>=len(self.grid) or k>=len(self.grid[0]) or k<0 or self.grid[r][k] in ['G', 'W']:
            return
        # print('Called, down', r,k)
        self.grid[r][k]= 'X'
        self.down_break(r+1, k)


    def left_break(self,r,k):
        if r<0 or r>=len(self.grid) or k>=len(self.grid[0]) or k<0 or self.grid[r][k] in ['G', 'W']:
            return
        # print('Called left', r, k)
        self.grid[r][k]= 'X'
        self.left_break(r, k-1)


    def right_break(self,r,k):
        if r<0 or r>=len(self.grid) or k>=len(self.grid[0]) or k<0 or self.grid[r][k] in ['G', 'W']:
            return
        # print('Called right', r,k)
        self.grid[r][k]= 'X'
        self.right_break(r, k+1)
