fn helper(memo: &mut Vec<Vec<i32>>, flag: &mut bool, r:usize, c:usize) {
    match memo[r][c] {
        1 => {
            if *flag {
                memo[r][c] = 0;
            }
        },
        10 => {
            *flag = true;
        },
        20 => {
            *flag = false;
        }
        _ => {}
    }
} 

impl Solution {
    pub fn count_unguarded(m: i32, n: i32, guards: Vec<Vec<i32>>, walls: Vec<Vec<i32>>) -> i32 {
        let m = m as usize;
        let n = n as usize;
        let mut memo = vec![vec![1;n];m];

        for arr in guards {
            memo[arr[0] as usize][arr[1] as usize] = 10;
        }
        for arr in walls {
            memo[arr[0] as usize][arr[1] as usize] = 20;
        }

        for i in 0..m {
            let mut flag = false;
            for j in 0..n {
                helper(&mut memo, &mut flag, i, j);
            } 
            let mut flag = false;
            for j in (0..n).rev() {
                helper(&mut memo, &mut flag, i, j);
            }
        }

        for i in 0..n {
            let mut flag = false;
            for j in 0..m {
                helper(&mut memo, &mut flag, j, i);
            } 
            let mut flag = false;
            for j in (0..m).rev() {
                helper(&mut memo, &mut flag, j, i);
            }
        }

        let mut result = 0;
        for i in 0..m {
            for j in 0..n {
                if memo[i][j] == 1 {
                    result += 1;
                }
            }
        }

        result
    }
}
