class Solution {
public:
    int countUnguarded(int m, int n, vector<vector<int>>& guards, vector<vector<int>>& walls) {
        vector<vector<int>> visited(m+1, vector<int> (n+1, 0));
        int ans = 0;
        for(vector<int> &vec : walls){
            visited[vec[0]][vec[1]] = -1;
            ans++;
        }
        int dirx[4] = {-1,1,0,0};
        int diry[4] = {0,0,1,-1};
        queue<pair<pair<int, int>, int>> q;
        for(vector<int> &vec : guards)
        {
            visited[vec[0]][vec[1]] = 2;
            q.push({{vec[0], vec[1]},4});
        }
        while(!q.empty())
        {
            pair<pair<int, int>, int> tp = q.front();
            q.pop();
            int x = tp.first.first;
            int y = tp.first.second;
            int dir = tp.second;
            if(visited[x][y] != -2)
                ans++;
            if(dir == 4)
            {
                for(int i = 0; i < 4; i++)
                {
                    int nxtx = x + dirx[i];
                    int nxty = y + diry[i];
                    if(nxtx >= m || nxtx < 0 || nxty >= n || nxty < 0) continue;
                    if(visited[nxtx][nxty] == -1 || visited[nxtx][nxty] == 2) continue;
                    q.push({{nxtx, nxty}, i});
                }
            }
            else
            {
                visited[x][y] = -2;
                int nxtx = x + dirx[dir];
                int nxty = y + diry[dir];
                if(nxtx >= m || nxtx < 0 || nxty >= n || nxty < 0) continue;
                if(visited[nxtx][nxty] == -1 || visited[nxtx][nxty] == 2) continue;
                q.push({{nxtx, nxty}, dir});
            }
        }
        return (long long)(m*n) - ans;
    }
};
