class Solution {
    private final char GUARD = 'G';
    private final char RANGE = 'R';
    private final char WALL = 'W';
    private final int[][] dirs = {{0, 1}, {1, 0}, {-1, 0}, {0, -1}};

    public int countUnguarded(int m, int n, int[][] guards, int[][] walls) {
        final char[][] mat = new char[m][n];
        for (final int[] wall : walls) {
            mat[wall[0]][wall[1]] = WALL;
        }

        for (final int[] guard : guards) {
            mat[guard[0]][guard[1]] = GUARD;
        }

        int count = (m*n) - guards.length - walls.length;

        for (final int[] guard : guards) {
            for (final int[] dir : dirs) {
                int x = dir[0] + guard[0], y = dir[1] + guard[1];
                while (isValid(mat, x, y)) {
                    if (mat[x][y] == 0) count--;
                    mat[x][y] = RANGE;
                    x += dir[0];
                    y += dir[1]; 
                }
            }
        }

        return count;
    }

    private boolean isValid(final char[][] mat, final int i, final int j) {
