class Solution {

    enum state {
        GUARDED,
        WALL,
        NONE
    };

    vector<vector<state>> grid_;
    int rows_;
    int cols_;

    void guard(int x, int y) {
        int cur_x = x;
        // grid_[x][y] = GUARDED;
        while (cur_x >= 0) {
            if (grid_[cur_x][y] == WALL)
                break;

            grid_[cur_x][y] = GUARDED;
            --cur_x;
        }

        cur_x = x;
        while (cur_x < rows_) {
            if (grid_[cur_x][y] == WALL)
                break;

            grid_[cur_x][y] = GUARDED;
            ++cur_x;
        }

        int cur_y = y;
        while (cur_y >= 0) {
            if (grid_[x][cur_y] == WALL)
                break;
            grid_[x][cur_y] = GUARDED;
            --cur_y;
        }

        cur_y = y;
        while (cur_y < cols_) {
            if (grid_[x][cur_y] == WALL)
                break;
            grid_[x][cur_y] = GUARDED;
            ++cur_y;
        }
    }

public:
    int countUnguarded(int m, int n, vector<vector<int>>& guards, vector<vector<int>>& walls) {
        grid_.assign(m, vector<state>(n, NONE));
        rows_ = m;
        cols_ = n;

        for (auto& w : walls) {
            auto x = w[0];
            auto y = w[1];
            grid_[x][y] = WALL;
        }

        for (auto& g : guards) {
            guard(g[0], g[1]);
        }

        int res = 0;
        for (int i = 0; i < m; ++i) {
            for (int j = 0; j < n; ++j) {
                if (grid_[i][j] == NONE)
                    ++res;
            }
        }

        return res;
    }
};
