class Solution 
{
    //0----->unvisited  //1---->visited  //2----->guard  //3----->wall
    int d[][]={{-1,0},{1,0},{0,1},{0,-1}};

    public int countUnguarded(int m, int n, int[][] g, int[][] w) 
    {
        int ans=0; int visited[][]=new int[m][n];

        for(int i=0;i<w.length;i++)
        {
            int x=w[i][0];  int y=w[i][1]; visited[x][y]=3;
        }

        for(int i=0;i<g.length;i++)
        {
            int x=g[i][0];  int y=g[i][1];  visited[x][y]=2;
        }

        for(int i=0;i<g.length;i++)
        {
            int x=g[i][0];  int y=g[i][1];

            for(int j=0;j<4;j++)
            dfs(visited,x+d[j][0],y+d[j][1],d[j][0],d[j][1]);
        }

        for(int i=0;i<m;i++)
        {
            for(int j=0;j<n;j++)
            {
                if(visited[i][j]==0)
                ans++;
            }
        }

        return ans;
    }

    public void dfs(int visited[][],int i,int j,int x,int y)
    {
        if(i<0 || j<0 || i>=visited.length || j>=visited[0].length)
        return;

