class Solution {
    public int percentageLetter(String s, char letter) {
        int ans;
        int count = 0;
        int numOfString = s.length();
        char[] chr = new char[numOfString];
        for(int i=0; i<chr.length; i++){
            chr[i] = s.charAt(i);
        }
        for(int i=0; i<chr.length; i++){
            if(chr[i] == letter){
                count++;
            }
        }
        ans = (count*100)/numOfString;
        return ans;
    }
}
