var percentageLetter = function(s, letter) {
    let includedCount = 0;
    if (!letter.includes(s)) return 0; // check if letter does not include s at all
    for (let l of s) {
        if (l == letter) includedCount+=1; // check if 'l' is the same as letter if so, add 1 to 'includedCount'
    }
    let result = eval(includedCount / s.length * 100).toString() // evaluate an equation and stringify the result
    if (result.includes(".")) result = parseInt(result.split(".")[0]) // remove the decimal
    return result;
};
```
