class Solution {
public:
    double calculateTax(vector<vector<int>>& brackets, int income) 
    {
        // if income is nothing then tax will be 0
        if(income == 0) 
            return 0.0;

        double ans = 0;
        int prev = 0;                    // for storing the previous earning

        for(auto x : brackets)
        {
            // take the min earning first for calculating tax charge on that
            int curr = min(x[0],income);

            // add charges in total amout
            ans += ((curr-prev)*x[1]/100.0);

            // if money exceed the income break and return tax amout
            if(x[0] >= income) break;

            // storing current money in previous
            prev = x[0];
        }
        return ans;
    }
};
```

