class Solution {
    public double calculateTax(int[][] brackets, int income) {
        double taxes = 0;
        if (income==0) return 0;//No tax for 0 income
		//Now checking for the first tax bracket, the range will start from zero itself
        double range = brackets[0][0];
        if (income>=range){
		//Let's say that the range is 3 and income is 10, we would have to check for the next tax bracket after caculating the tax value till 3
            double taxPercent = range * brackets[0][1] / 100;
            taxes +=taxPercent;//Adding the tax of this bracket into the total tax to be paid
            income -= range;//Remaining income to be taxed
        }else if (income>0){
		//Let's say that the income is 2 and range is 3, so we don't need any other tax bracket to be taken care of. Here, we can directly return the taxes
            double inc = income;
            double taxPercent = inc * brackets[0][1] / 100;
            taxes +=taxPercent;
            return taxes;
        }
        for (int i = 1; i < brackets.length ; i++) {
		//Entering the loop when the income is greater than the first bracket
            range = brackets[i][0] - brackets[i-1][0];//Range of a particular bracket
            if (income>=range){
			//We would have to go to the next bracket as well (if present)
			//So calculating the taxes of this bracket and moving to the next bracket
                double taxPercent = range * brackets[i][1] / 100;
                taxes +=taxPercent;
                income -= range;
            }else if (income>0){
			//Else the income is less than the current range. So calculating the tax of this bracket and then breaking the loop
                double inc = income;
                double taxPercent = inc * brackets[i][1] / 100;
                taxes +=taxPercent;
                income=0;
				break;
            }
        }

        return taxes;//Total amount of tax to be paid is now returned
    }
}

