public:
    double calculateTax(vector<vector<int>>& brackets, int income) {
        int n = brackets.size(); 
        double taxes = 0; 
        int lowerbound = 0; 
        for(int i=0; i<n; i++) {
            int upperbound = brackets[i][0]; 
            int taxes_per = brackets[i][1]; 

            // Case 1 - When income is greater than or equal upperbound 
            // Case 2 - When income is not greater than upperbound but greater than the lowerbound 
            if(income >= upperbound) {
                int taxable = upperbound - lowerbound;
                taxes += (taxable * taxes_per) / 100.0;
                lowerbound = upperbound;
            } else if(income < upperbound && income > lowerbound) {
                int taxable = income - lowerbound; 
                taxes += (taxable * taxes_per) / 100.0; 
                break;
            }
        }
        return taxes;
    }
};
