class Solution {
    public double calculateTax(int[][] brackets, int income) {
        int len = brackets.length;  // to calculate length of given array ('brackets' here)
        double ans = 0;  // to keep track of final answer
        int past = 0;  // previous taxable value

        for(int i=0; i<len; i++) {
            int decider = Math.min(income, brackets[i][0]);  // to get minimum between income and current taxable value
            ans += (double)(decider-past)*(double)(brackets[i][1])/100.0; // to calculate tax and adding it to our answer
            past = brackets[i][0]; // to change previous taxable value
            if(income <= brackets[i][0]) break; // to break the loop, if our income is less than current taxable value
        }
        return ans; // finally, returning ans i.e:- total amount paid in taxes
    }
}
