var calculateTax = function(brackets, income) {
    let result = 0;
    let count = 0;

    for(let i = 0; i < brackets.length; i++) {
        if(count === income) break;

        const [dollars, tax] = brackets[i];

        let diff = 0;

        if(dollars > income) {
            diff = dollars - income;
        }

        result += ((dollars - count - diff) / 100) * tax;
        count = dollars - diff;
    }


    return result;
};
