/**
 * @param {number[][]} brackets
 * @param {number} income
 * @return {number}
 */
var calculateTax = function(brackets, income) {
    let totalTaxesPaid = 0; 
    let taxAppliedOn = 0;
    brackets.forEach((bracket) => {
        const [limit, percentage] = bracket; 
        const valueToUse = Math.min(income, limit - taxAppliedOn); 
        totalTaxesPaid += valueToUse * (percentage / 100)
        income -= valueToUse; 
        taxAppliedOn += valueToUse; 
    });

    return totalTaxesPaid;
};
```

