/**
 * @param {number[][]} brackets
 * @param {number} income
 * @return {number}
 */
var calculateTax = function (brackets, income) {
    let result = 0
    let past = 0
    for (let i = 0; i < brackets.length; i++) {
        const bracket = brackets[i]
        const diff = bracket[0] - past
        const multiplier = bracket[1] / 100
        if (diff < income) {
            result += diff * multiplier
            income -= diff
        } else {
            result += income * multiplier
            income = 0
        }
        if (income === 0) break
        past = bracket[0]
    }

    return result + income
};
