public class Solution {
    public double CalculateTax(int[][] brackets, int income) {

        double totalTax = 0.0;
        if (income <= 0) return totalTax;

        int index = 0;
        int prevAmount = 0;
        int currentAmount = 0;

        while (income > 0)
        {
            currentAmount = brackets[index][0];

            totalTax += taxAmount(currentAmount - prevAmount, index);

            index++;
            prevAmount = currentAmount;

        }
        return totalTax;

        // helper function
        double taxAmount(int amount, int pointer)
        {
            if (income >= amount)
            {
                income -= amount;
                return amount * brackets[pointer][1] * 0.01;
            }
            else
            {
                int remainder = income; // a temp variable for calculation
                income = 0;
                return remainder * brackets[pointer][1] * 0.01;
            }

        }
    }
}
```

