    public double calculateTax(int[][] brackets, int income) {
        int i = 0; //move through tax salb
        double left = income; // left salary
        double tax = 0.0;
        double limit = 0.0;

        while(left > 0){
            int [] slab = brackets[i];
            double per = slab[1];
            // if(slab[0] == null){
