public class Solution {
    public double CalculateTax(int[][] brackets, int income) 
    {
        var tax = 0.0;

        for(int i=0, prevUpper=0; income > 0; i++)
        {
            var (upper, percent) = (brackets[i][0], brackets[i][1]);

            var toTax = Math.Min(income, upper - prevUpper);

            tax += toTax * percent / 100.0;

            income -= toTax;

            prevUpper = upper;
        }

        return tax;
    }
}
